require 'spec_helper'

describe Tapyrus::Message::BlockTxn do
  describe '#parse_from_payload' do
    subject do
      Tapyrus::Message::BlockTxn.parse_from_payload(
        ''
          .htb
      )
    end
    it 'should be parsed.' do
      expect(subject.block_transactions.block_hash).to eq(
        'ca8fb932735c229005040eeefed540f36dfab54c68acd92e54ad69c200000000'
      )
      expect(subject.block_transactions.transactions.size).to eq(5)
      expect(subject.block_transactions.transactions[0].txid).to eq(
        '2377df364db3b56196ec59fd397084740bd8b9124bb1194cfd78f15034a0193a'
      )
      expect(subject.block_transactions.transactions[1].txid).to eq(
        '31eb55aef69814ff1a212fb7342981d46eb7bd9982e30c4856255f850410168e'
      )
      expect(subject.block_transactions.transactions[2].txid).to eq(
        '725ad4bd42a1cf3d5357ea6ccb699773ffc8d852bbe71f0c5b39aa17b135b75e'
      )
      expect(subject.block_transactions.transactions[3].txid).to eq(
        '9bdb4b06ed0236b6a9ce455d4d2e9b6e3e2f347312d299a705eec01dcfa98c01'
      )
      expect(subject.block_transactions.transactions[4].txid).to eq(
        '765653797ecb18da16289265a6045a9303270bb769dfec357a343d374e233319'
      )
      expect(subject.to_hex).to eq(
        ''
      )
    end
  end
end
