require 'spec_helper'

describe Tapyrus::Message::CmpctBlock do

  describe '#parse_from_payload' do
    subject {
      Tapyrus::Message::CmpctBlock.parse_from_payload(''.htb)
    }
    it 'should be parsed.' do
      expect(subject.header_and_short_ids.header.block_hash).to eq('962a5d4b1be807e972185b15660f268eb384e9b7e2409f8c1d0af330c6243b17')
      expect(subject.header_and_short_ids.header.to_hex).to eq('')
      expect(subject.header_and_short_ids.nonce).to eq(0)
      expect(subject.header_and_short_ids.short_ids[0]).to eq(25537143879877)
      expect(subject.header_and_short_ids.short_ids[1]).to eq(210764076828674)
      expect(subject.header_and_short_ids.short_ids.size).to eq(2)
      expect(subject.header_and_short_ids.prefilled_txn.size).to eq(4)
      expect(subject.header_and_short_ids.prefilled_txn.first.index).to eq(0)
      expect(subject.header_and_short_ids.siphash_key.bth).to eq('d168e2c95b1e1a5c6edbb73b7d7b3874')
      expect(subject.header_and_short_ids.prefilled_txn.first.tx.txid).to eq('fd6c9bcdc9d1070cb40c29e1764c84e27c18391cb5d9e89305e95514cb1129b4')
      expect(subject.to_hex).to eq('')
    end
  end

  describe '#from_block' do
    let(:block) {
      Tapyrus::Message::Block.parse_from_payload(load_block('75be7b3b19e07f2c3644523016132db7e7e67063b3f5abbaa420cafc8a44557f').htb).to_block
    }

    context 'version 1' do
      subject {
        Tapyrus::Message::CmpctBlock.from_block(block, 7907921748630648002)
      }
      it 'should generate CmpctBlock.' do
        expect(subject.header_and_short_ids.short_ids[0]).to eq(254701722717646)
        expect(subject.header_and_short_ids.short_ids.size).to eq(1)
        expect(subject.header_and_short_ids.prefilled_txn.size).to eq(1)
        expect(subject.to_hex).to eq('')
      end
    end
  end

end
