require 'spec_helper'

describe Tapyrus::Message::HeaderAndShortIDs do

  describe '#short_id' do
    subject {
      Tapyrus::Message::HeaderAndShortIDs.parse_from_payload(''.htb)
    }
    it 'should calculate transaction short id.' do
      expect(subject.short_id('282cf67861a83768c9321496aa35e6a720442a53e87780abca74a5f5919f405a')).to eq(131728108278982)
      expect(subject.short_id('356ecf9f4c9961c6a265e08159770f0f2bb539e946048bd7579a1b3b14734617')).to eq(161020309083421)
    end
  end

end