require 'spec_helper'

describe Tapyrus::Message::Tx do
  describe 'parse from payload' do
    context 'no use segwit' do
      # MSG_TX
      subject do
        Tapyrus::Message::Tx.parse_from_payload(
          ''
            .htb
        )
      end
      it 'should be parsed' do
        expect(subject.tx.txid).to eq('1936bc092efe3dc36d2ec8bcadb4fa58bef98015b7fa3928b14731f02676ca0c')
        expect(subject.tx.inputs.length).to eq(5)
        expect(subject.tx.outputs.length).to eq(2)
      end
    end
  end

  describe '#to_payload' do
    subject do
      tx =
        Tapyrus::Tx.parse_from_payload(
          ''
            .htb
        )
      Tapyrus::Message::Tx.new(tx).to_pkt
    end
    it 'should be parsed' do
      expect(subject).to eq(
        ''
          .htb
      )
    end
  end
end
